INSERT INTO `document_types` VALUES (1,'Salary Slip','SALARY_SLIP','fas fa-receipt','Monthly employee payslip','<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Salary Slip - {{Month}}</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n      font-family: var(--font-family);\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f5f5f5;\n      font-size: 11px;\n      color: var(--primary-text);\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      border: 1px solid #d0d0d0;\n      position: relative;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        border: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n        border: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER (same style family as other letters) */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 10px;\n      color: var(--muted-text);\n      line-height: 1.4;\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n      text-transform: none;\n      letter-spacing: 0.5px;\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    .separator {\n      margin: 10px 0;\n      border-top: 1px solid #999;\n    }\n\n    .title-bar {\n      text-align: center;\n      margin: 10px 0 12px 0;\n      font-size: 13px;\n      font-weight: bold;\n      text-decoration: underline;\n    }\n\n    .section-title {\n      font-weight: bold;\n      margin: 10px 0 5px 0;\n      font-size: 12px;\n      text-transform: uppercase;\n    }\n\n    .info-table {\n      width: 100%;\n      border-collapse: collapse;\n      margin-bottom: 4px;\n    }\n\n    .info-table td {\n      padding: 2px 4px;\n      vertical-align: top;\n      font-size: 11px;\n    }\n\n    .info-label {\n      width: 35%;\n      white-space: nowrap;\n    }\n\n    .info-label span {\n      font-weight: bold;\n    }\n\n    .info-value {\n      width: 65%;\n    }\n\n    .info-value span {\n      padding-left: 4px;\n    }\n\n    .salary-title {\n      margin-top: 12px;\n      font-weight: bold;\n      font-size: 12px;\n    }\n\n    .sal