INSERT INTO `email_templates` VALUES (1,'OFFER_LETTER_DEFAULT','Offer Letter Default','Your Offer Letter - {{employeeName}}','<p>Dear {{employeeName}},</p><p>Congratulations! Please find your offer letter attached.</p>',8,1,1,'2025-12-06 16:10:36','2025-12-23 14:20:23','2025-12-23 14:20:23'),(2,'SALARY_SLIP_DEFAULT','Salary Slip  Monthly','Salary Slip for {{monthYear}} - {{employeeName}}','<p>Dear {{employeeName}},</p>\n\n<p>\n  Please find attached your salary slip for \n  <strong>{{monthYear}}</strong>.\n</p>\n\n<p>\n  This slip summarises your earnings, deductions and net pay for the month.\n  We recommend you keep this email and the attached salary slip for your\n  personal records and any future reference.\n</p>\n\n<p>\n  If you notice any discrepancy or have questions regarding your salary\n  structure, deductions (PF/ESI/TDS) or reimbursements, please reply to\n  this email or raise a ticket with the HR / Payroll team.\n</p>\n\n<p>\n  Regards,<br/>\n  HR & Payroll Team<br/>\n  Seecog Softwares Pvt. Ltd.\n</p>\n\n<hr style=\"border:none;border-top:1px solid #ddd;margin:16px 0;\" />\n\n<p style=\"font-size:12px;color:#666;\">\n  <strong>Note:</strong> This is a system-generated email for sharing your\n  monthly salary slip. If you have already received this slip from another\n  channel, you can ignore this message.\n</p>\n',1,1,1,'2025-12-07 06:18:59','2025-12-23 14:20:10','2025-12-23 14:20:10'),(3,'First_Greet_Template','First Greet Template','Scaling your delivery with pre-vetted engineering talent (Staff Augmentation)','Hi Team,\n\nWe are  from Seecog Softwares Private Limited (Bengaluru, India). We help product companies and agencies scale delivery through IT staffing / staff augmentation and dedicated engineering teams.\n\nIf youre currently hiring or planning to scale, we can support you with pre-vetted resources across:\n\nBackend: Java (Spring Boot), Node.js (NestJS/Express), Go (Gin/gRPC)\n\n\nFrontend: React / Next.js\n\n\nDevOps: AWS, Docker, Kubernetes, CI/CD\n\n\nQA: Manual + Automation\n\n\nHow we typically work:\n\n12 candidate profiles shared within 4872 hours\n\n\nInterviews coordinated as per your process\n\n\nFlexible engagement: hourly / monthly, part-time or full-time\n\n\nOptional: replacement support during the initial period\n\n\nIf you can share a brief requirement (role, stack, experience, timezone overlap, budget), Ill respond with relevant profiles and next steps.\n\nWould you be open to a 1520 minute call this week to understand your needs and confirm the engagement model?',NULL,0,1,'2025-12-23 14:23:28','2025-12-23 14:51:11','2025-12-23 14:51:11'),(4,'First_Greet_Email','First Greet Email','Scaling your delivery with pre-vetted engineering talent (Staff Augmentation)','<!doctype html>\n<html lang=\"en\">\n  <head>\n    <meta charset=\"utf-8\" />\n    <meta name=\"viewport\" content=\"width=device-width,initial-scale=1\" />\n    <meta name=\"x-apple-disable-message-reformatting\" />\n    <meta name=\"format-detection\" content=\"telephone=no,address=no,email=no,date=no,url=no\" />\n    <title>Seecog Softwares  IT Staffing / Dedicated Teams</title>\n\n    <!--\n      Email notes:\n      - Keep CSS minimal + inline-friendly\n      - Avoid JS, complex gradients, external fonts\n      - Buttons are built with table cells for better Outlook support\n    -->\n    <style>\n      /* Some clients honor this; many ignore. Still helpful. */\n      @media (prefers-color-scheme: dark) {\n        body { background-color: #0b1220 !important; }\n      }\n      @media screen and (max-width: 620px) {\n        .container { width: 100% !important; }\n        .px { padding-left: 16px !important; padding-right: 16px !important; }\n        .stack { display: block !important; width: 100% !important; }\n        .pt-sm { padding-top: 12px !important; }\n        .center-sm { text-align: center !important; }\n      }\n    </style>\n  </head>\n\n  <body style=\"margin:0; padding:0; background:#0b1220;\">\n    <!-- Prehead