INSERT INTO `document_types` VALUES (1,'Salary Slip','SALARY_SLIP','fas fa-receipt','Monthly employee payslip','<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Salary Slip - {{Month}}</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n      font-family: var(--font-family);\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f5f5f5;\n      font-size: 11px;\n      color: var(--primary-text);\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      border: 1px solid #d0d0d0;\n      position: relative;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        border: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n        border: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER (same style family as other letters) */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 10px;\n      color: var(--muted-text);\n      line-height: 1.4;\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n      text-transform: none;\n      letter-spacing: 0.5px;\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    .separator {\n      margin: 10px 0;\n      border-top: 1px solid #999;\n    }\n\n    .title-bar {\n      text-align: center;\n      margin: 10px 0 12px 0;\n      font-size: 13px;\n      font-weight: bold;\n      text-decoration: underline;\n    }\n\n    .section-title {\n      font-weight: bold;\n      margin: 10px 0 5px 0;\n      font-size: 12px;\n      text-transform: uppercase;\n    }\n\n    .info-table {\n      width: 100%;\n      border-collapse: collapse;\n      margin-bottom: 4px;\n    }\n\n    .info-table td {\n      padding: 2px 4px;\n      vertical-align: top;\n      font-size: 11px;\n    }\n\n    .info-label {\n      width: 35%;\n      white-space: nowrap;\n    }\n\n    .info-label span {\n      font-weight: bold;\n    }\n\n    .info-value {\n      width: 65%;\n    }\n\n    .info-value span {\n      padding-left: 4px;\n    }\n\n    .salary-title {\n      margin-top: 12px;\n      font-weight: bold;\n      font-size: 12px;\n    }\n\n    .salary-table {\n      width: 100%;\n      border-collapse: collapse;\n      margin-top: 6px;\n      font-size: 11px;\n    }\n\n    .salary-table th,\n    .salary-table td {\n      border: 1px solid #000;\n      padding: 4px 5px;\n    }\n\n    .salary-table th {\n      background: #f0f0f0;\n      font-weight: bold;\n      text-align: center;\n    }\n\n    .salary-table td {\n      text-align: left;\n    }\n\n    .salary-table .right {\n      text-align: right;\n    }\n\n    .net-salary {\n      margin-top: 12px;\n      font-weight: bold;\n      font-size: 12px;\n    }\n\n    .footer-row {\n      display: flex;\n      justify-content: space-between;\n      align-items: flex-end;\n      margin-top: 18px;\n      gap: 16px;\n    }\n\n    .footer-note {\n      font-size: 9px;\n      color: #555;\n      max-width: 60%;\n    }\n\n    .signature-block {\n      text-align: right;\n      font-size: 10px;\n    }\n\n    .signature-block p {\n      margin: 2px 0;\n    }\n\n    .signature-images {\n      margin-top: 6px;\n      display: flex;\n      justify-content: flex-end;\n    }\n\n    .signature-images img {\n      max-height: 50px;\n      width: auto;\n      display: block;\n    }\n  </style>\n</head>\n<body>\n  <div class=\"a4-sheet\">\n    <!-- HEADER with logo -->\n    <header class=\"letter-header\">\n      <div class=\"logo-wrapper\">\n        <img src=\"{{LOGO_SRC}}\" alt=\"Seecog Softwares Pvt. Ltd. Logo\" />\n      </div>\n\n      <div class=\"company-details\">\n        <h1>Seecog Softwares Pvt. Ltd.</h1>\n        <div class=\"cin\">\n          CIN: U7220JH2021PTC017350\n        </div>\n        <div>\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi,<br />\n          Koramangala, Bengaluru, Karnataka 560030, India\n        </div>\n        <div>\n          Website:\n          <a href=\"https://seecogsoftwares.com\" target=\"_blank\">seecogsoftwares.com</a>\n        </div>\n        <div>Email: info@seecogsoftwares.com</div>\n        <div>Contact: +91-7625067691</div>\n      </div>\n    </header>\n\n    <div class=\"separator\"></div>\n\n    <!-- Title -->\n    <div class=\"title-bar\">\n      Pay Slip for the Month of {{Month}}\n    </div>\n\n    <!-- Employee Info -->\n    <div class=\"section-title\">Employee Details</div>\n    <table class=\"info-table\">\n      <tr>\n        <td class=\"info-label\">\n          <span>Employee Name</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{EMP_NAME}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Designation</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{DESIGNATION}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Department</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{DEPARTMENT}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Location</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{LOCATION}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Employee Code</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{EMP_CODE}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Currency</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: INR</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Date of Birth</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{DOB}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Date of Joining</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{DOJ}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Job Type</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: Full Time</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>PAN</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{PAN}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>No. of Days Paid</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{DAYS_PAID}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>No. of Working Days</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{WORK_DAYS}}</span>\n        </td>\n      </tr>\n      <tr>\n        <td class=\"info-label\">\n          <span>Salary Slip Date</span>\n        </td>\n        <td class=\"info-value\">\n          <span>: {{SALARY_SLIP_DATE}}</span>\n        </td>\n      </tr>\n      \n      \n    </table>\n\n    <!-- Salary Details -->\n    <div class=\"salary-title\">Salary Details</div>\n\n    <table class=\"salary-table\">\n      <tr>\n        <th colspan=\"3\">Earnings (₹)</th>\n        <th colspan=\"2\">Deductions (₹)</th>\n      </tr>\n      <tr>\n        <th>Components</th>\n        <th>Actual Rate (₹)</th>\n        <th>Earnings (₹)</th>\n        <th>Components</th>\n        <th>Deductions (₹)</th>\n      </tr>\n      <tr>\n        <td>Basic</td>\n        <td class=\"right\">{{BASIC}}</td>\n        <td class=\"right\">{{BASIC}}</td>\n        <td>Provident Fund (PF)</td>\n        <td class=\"right\">{{PF}}</td>\n      </tr>\n      <tr>\n        <td>H.R.A</td>\n        <td class=\"right\">{{HRA}}</td>\n        <td class=\"right\">{{HRA}}</td>\n        <td>ESI</td>\n        <td class=\"right\">{{ESI}}</td>\n      </tr>\n      <tr>\n        <td>Special Allowance</td>\n        <td class=\"right\">{{SPECIAL}}</td>\n        <td class=\"right\">{{SPECIAL}}</td>\n        <td>Income Tax / TDS</td>\n        <td class=\"right\">{{TDS}}</td>\n      </tr>\n      <tr>\n        <td>Reimbursement</td>\n        <td class=\"right\">{{REIMBURSEMENT}}</td>\n        <td class=\"right\">{{REIMBURSEMENT}}</td>\n        <td>Professional Tax</td>\n        <td class=\"right\">{{PROFESSIONAL_TAX}}</td>\n      </tr>\n      <tr>\n        <td><strong>Total</strong></td>\n        <td class=\"right\"><strong>{{TOTAL_EARNINGS}}</strong></td>\n        <td class=\"right\"><strong>{{TOTAL_EARNINGS}}</strong></td>\n        <td><strong>Total</strong></td>\n        <td class=\"right\"><strong>{{TOTAL_DEDUCTIONS}}</strong></td>\n      </tr>\n    </table>\n\n    <div class=\"net-salary\">\n      Net Salary Payable: ₹{{NET_SALARY}}\n    </div>\n\n    <!-- Footer: note + stamp -->\n    <div class=\"footer-row\">\n      <div class=\"footer-note\">\n        This is a system generated salary slip and does not require a physical signature.\n      </div>\n\n      <div class=\"signature-block\">\n        <p><strong>For Seecog Softwares Pvt. Ltd.</strong></p>\n        <div class=\"signature-images\">\n          <img src=\"{{STAMP_SRC}}\" alt=\"Company Stamp\" />\n        </div>\n      </div>\n    </div>\n  </div>\n</body>\n</html>\n',0,'2025-11-28 06:24:56','2025-12-06 06:10:43',NULL),(6,'Internship Offer Letter','INTERNSHIP_OFFER','fas fa-file-contract','Formal internship offer letter issued to selected candidates, detailing role, duration, stipend, reporting manager, working hours, and basic terms and conditions.','<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Internship Offer Letter - {{FullName}}</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n      font-family: var(--font-family);\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f5f5f5;\n      font-size: 11px;\n      line-height: 1.6;\n      color: var(--primary-text);\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      position: relative;\n      border: 1px solid #d0d0d0;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        border: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n        border: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER (same style as Offer Letter) */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n      letter-spacing: 0.5px;\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    .line-separator {\n      margin: 10px 0;\n      border-top: 1px solid #999;\n    }\n\n    .meta-table {\n      width: 100%;\n      border-collapse: collapse;\n      margin-bottom: 12px;\n      font-size: 11px;\n    }\n\n    .meta-table td {\n      padding: 2px 0;\n      vertical-align: top;\n    }\n\n    .meta-label {\n      width: 60px;\n      font-weight: bold;\n      white-space: nowrap;\n    }\n\n    .meta-value {\n      padding-left: 4px;\n    }\n\n    .subject {\n      font-size: 13px;\n      font-weight: bold;\n      text-align: center;\n      margin: 10px 0 14px 0;\n      text-transform: none;\n      text-decoration: underline;\n    }\n\n    .body {\n      font-size: 11px;\n      text-align: justify;\n    }\n\n    .body p {\n      margin: 4px 0 8px 0;\n    }\n\n    .salutation {\n      margin-bottom: 8px;\n    }\n\n    .closing {\n      margin-top: 18px;\n    }\n\n    .closing p {\n      margin: 2px 0;\n    }\n\n    .signature-block {\n      margin-top: 18px;\n    }\n\n    .signature-images {\n      margin-top: 8px;\n      display: flex;\n      align-items: center;\n      gap: 12px;\n    }\n\n    .signature-images img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .signature-name {\n      margin-top: 22px;\n      font-weight: bold;\n    }\n\n    .signature-title {\n      margin-top: 2px;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .acceptance {\n      margin-top: 30px;\n      font-size: 11px;\n    }\n\n    .acceptance-title {\n      font-weight: bold;\n      margin-bottom: 8px;\n    }\n\n    .acceptance-line {\n      margin: 6px 0;\n    }\n  </style>\n</head>\n<body>\n  <div class=\"a4-sheet\">\n    <!-- HEADER with logo (same as Offer Letter) -->\n    <header class=\"letter-header\">\n      <div class=\"logo-wrapper\">\n        <img src=\"{{LOGO_SRC}}\" alt=\"Seecog Softwares Pvt. Ltd. Logo\" />\n      </div>\n\n      <div class=\"company-details\">\n        <h1>Seecog Softwares Pvt. Ltd.</h1>\n        <div class=\"cin\">\n          CIN: U7220JH2021PTC017350\n        </div>\n        <div>\n          Prestige Cube, Site No. 26, Laskar Hosur Rd, Adugodi,<br />\n          Koramangala, Bengaluru, Karnataka 560030, India\n        </div>\n        <div>\n          Website:\n          <a href=\"https://seecogsoftwares.com\" target=\"_blank\">seecogsoftwares.com</a>\n        </div>\n        <div>Email: info@seecogsoftwares.com</div>\n        <div>Contact No: +91-7625067691</div>\n      </div>\n    </header>\n\n    <div class=\"line-separator\"></div>\n\n    <!-- Name / Location -->\n    <table class=\"meta-table\">\n      <tr>\n        <td class=\"meta-label\">Name:</td>\n        <td class=\"meta-value\">{{FullName}}</td>\n      </tr>\n      <tr>\n        <td class=\"meta-label\">Location:</td>\n        <td class=\"meta-value\">\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi, Koramangala, Bengaluru, Karnataka 560030, India\n        </td>\n      </tr>\n    </table>\n\n    <!-- Subject -->\n    <div class=\"subject\">\n      Internship Offer Letter\n    </div>\n\n    <!-- Letter Body -->\n    <div class=\"body\">\n      <div class=\"salutation\">\n        Dear {{FullName}},\n      </div>\n\n      <p>\n        We are pleased to offer you the position of <strong>Intern-{{Designation}}</strong> at\n        <strong>Seecog Softwares Pvt. Ltd.</strong> Your selection is based on your academic\n        background and interest in gaining practical experience in the software industry.\n      </p>\n\n      <p>The details of your internship are as follows:</p>\n\n      <p>\n        Your internship will commence on <strong>{{StartDate}}</strong> and end on\n        <strong>{{EndDate}}</strong>. The duration of the internship is\n        <strong>{{NumberofMonths}}</strong>.\n      </p>\n\n      <p>\n        You will be working in the <strong>{{DepartmentName}}</strong> department and will report to\n        <strong>“{{SupervisorName}} / {{SupervisorDesignation}}”</strong>.\n      </p>\n\n      <p>\n        You are expected to follow the company’s standard working hours\n        <strong>“{{WorkingHours}}”</strong>.\n      </p>\n\n      <p>\n        You will be paid a monthly stipend of\n        <strong>₹{{Amount}} (Rupees {{AmountinWords}} only)</strong>, subject to satisfactory\n        attendance and performance.\n      </p>\n\n      <p>\n        Upon successful completion of the internship and submission of required reports or tasks,\n        you will be issued an Internship Completion Certificate.\n      </p>\n\n      <p>\n        During the course of your internship, you may be exposed to sensitive company information.\n        You are required to maintain confidentiality and not disclose such information during or\n        after your internship.\n      </p>\n\n      <p>\n        Professional behavior and adherence to company policies are expected during your internship\n        period.\n      </p>\n\n      <p>\n        Please sign and return a copy of this letter as a token of your acceptance of the terms and\n        conditions stated above.\n      </p>\n\n      <p>\n        We are excited to welcome you to <strong>Seecog Softwares Pvt. Ltd.</strong> and hope that\n        this internship will be a valuable learning experience for you.\n      </p>\n\n      <!-- Closing & Signature with Stamp -->\n      <div class=\"closing\">\n        <p>Your’s sincerely,</p>\n        <p><strong>For Seecog Softwares Pvt. Ltd.</strong></p>\n\n        <div class=\"signature-block\">\n          <div class=\"signature-images\">\n            <img src=\"{{STAMP_SRC}}\" alt=\"Company Stamp\" />\n          </div>\n\n          <div class=\"signature-name\">Ms. Sonam Agarwal</div>\n          <div class=\"signature-title\">HR Manager</div>\n          <div class=\"signature-title\">Email: sonam@seecogsoftwares.com</div>\n          <div class=\"signature-title\">Authorized Signatory</div>\n        </div>\n      </div>\n\n      <!-- Acceptance Section -->\n      <div class=\"acceptance\">\n        <div class=\"acceptance-title\">Accepted and Agreed</div>\n\n        <div class=\"acceptance-line\">\n          {{FullName}}\n        </div>\n        <div class=\"acceptance-line\">\n          Date: ___ / ___ / 2025\n        </div>\n      </div>\n    </div>\n  </div>\n</body>\n</html>\n',0,'2025-11-29 07:59:01','2025-12-01 10:13:00',NULL),(7,'Pre-Placement Offer Letter (PPO)','PRE_PLACEMENT_OFFER','fas fa-briefcase','Pre-Placement Offer letter issued to high-performing interns, confirming future full-time employment with details of role, employment type, work location, CTC, joining date, and acceptance instructions.','<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Pre-Placement Offer Letter - {{Name}}</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n      font-family: var(--font-family);\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f5f5f5;\n      font-size: 11px;\n      line-height: 1.6;\n      color: var(--primary-text);\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      position: relative;\n      border: 1px solid #d0d0d0;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        border: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n        border: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER (same as Offer Letter) */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n      letter-spacing: 0.5px;\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    .line-separator {\n      margin: 10px 0;\n      border-top: 1px solid #999;\n    }\n\n    .meta-table {\n      width: 100%;\n      border-collapse: collapse;\n      margin-bottom: 12px;\n      font-size: 11px;\n    }\n\n    .meta-table td {\n      padding: 2px 0;\n      vertical-align: top;\n    }\n\n    .meta-label {\n      width: 100px;\n      font-weight: bold;\n      white-space: nowrap;\n    }\n\n    .meta-value {\n      padding-left: 4px;\n    }\n\n    .subject {\n      font-size: 13px;\n      font-weight: bold;\n      text-align: center;\n      margin: 10px 0 14px 0;\n      text-transform: none;\n      text-decoration: underline;\n    }\n\n    .body {\n      font-size: 11px;\n      text-align: justify;\n    }\n\n    .body p {\n      margin: 4px 0 8px 0;\n    }\n\n    .salutation {\n      margin-bottom: 8px;\n    }\n\n    .details-table {\n      width: 100%;\n      border-collapse: collapse;\n      margin: 8px 0 12px 0;\n      font-size: 11px;\n    }\n\n    .details-table td {\n      padding: 3px 0;\n      vertical-align: top;\n    }\n\n    .details-label {\n      width: 130px;\n      font-weight: bold;\n      white-space: nowrap;\n    }\n\n    .details-value {\n      padding-left: 4px;\n    }\n\n    .conditions-list {\n      margin: 6px 0 10px 18px;\n      padding: 0;\n    }\n\n    .conditions-list li {\n      margin-bottom: 4px;\n    }\n\n    .closing {\n      margin-top: 18px;\n    }\n\n    .closing p {\n      margin: 2px 0;\n    }\n\n    .signature-block {\n      margin-top: 18px;\n    }\n\n    .signature-images {\n      margin-top: 8px;\n      display: flex;\n      align-items: center;\n      gap: 12px;\n    }\n\n    .signature-images img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .signature-name {\n      margin-top: 22px;\n      font-weight: bold;\n    }\n\n    .signature-title {\n      margin-top: 2px;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n  </style>\n</head>\n<body>\n  <div class=\"a4-sheet\">\n    <!-- HEADER with logo -->\n    <header class=\"letter-header\">\n      <div class=\"logo-wrapper\">\n        <img src=\"{{LOGO_SRC}}\" alt=\"Seecog Softwares Pvt. Ltd. Logo\" />\n      </div>\n\n      <div class=\"company-details\">\n        <h1>Seecog Softwares Pvt. Ltd.</h1>\n        <div class=\"cin\">\n          CIN: U7220JH2021PTC017350\n        </div>\n        <div>\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi,<br />\n          Koramangala, Bengaluru, Karnataka 560030, India\n        </div>\n        <div>\n          Website:\n          <a href=\"https://seecogsoftwares.com\" target=\"_blank\">seecogsoftwares.com</a>\n        </div>\n        <div>Email: info@seecogsoftwares.com</div>\n        <div>Contact No: +91-7625067691</div>\n      </div>\n    </header>\n\n    <div class=\"line-separator\"></div>\n\n    <!-- Candidate Meta (same placeholders as your original) -->\n    <table class=\"meta-table\">\n      <tr>\n        <td class=\"meta-label\">Candidate Name:</td>\n        <td class=\"meta-value\">{{Name}}</td>\n      </tr>\n      <tr>\n        <td class=\"meta-label\">Designation:</td>\n        <td class=\"meta-value\">{{Designation}}</td>\n      </tr>\n      <tr>\n        <td class=\"meta-label\">Location:</td>\n        <td class=\"meta-value\">\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi, Koramangala, Bengaluru, Karnataka 560030, India\n        </td>\n      </tr>\n    </table>\n\n    <!-- Subject -->\n    <div class=\"subject\">\n      Pre-Placement Offer (PPO)\n    </div>\n\n    <!-- Letter Body -->\n    <div class=\"body\">\n      <div class=\"salutation\">\n        Dear {{Name}},\n      </div>\n\n      <p>\n        We are pleased to inform you that, based on your outstanding performance during your internship at\n        <strong>Seecog Softwares Pvt. Ltd.</strong>, we would like to extend to you a\n        <strong>Pre-Placement Offer (PPO)</strong> for the position of <strong>{{Designation}}</strong>\n        at our Bengaluru office.\n      </p>\n\n      <p>\n        This offer stands as a recognition of your professional conduct, technical proficiency, and alignment\n        with our company’s values and expectations.\n      </p>\n\n      <!-- PPO Details -->\n      <table class=\"details-table\">\n        <tr>\n          <td class=\"details-label\">Position:</td>\n          <td class=\"details-value\">{{Designation}}</td>\n        </tr>\n        <tr>\n          <td class=\"details-label\">Employment Type:</td>\n          <td class=\"details-value\">{{EmployeeType}}</td>\n        </tr>\n        <tr>\n          <td class=\"details-label\">Work Location:</td>\n          <td class=\"details-value\">\n            Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi, Koramangala, Bengaluru, Karnataka 560030, India\n          </td>\n        </tr>\n        <tr>\n          <td class=\"details-label\">Annual CTC:</td>\n          <td class=\"details-value\">{{CTC}}</td>\n        </tr>\n        <tr>\n          <td class=\"details-label\">Joining Date:</td>\n          <td class=\"details-value\">{{JoiningDate}}</td>\n        </tr>\n      </table>\n\n      <p>\n        Your CTC includes a fixed monthly salary of <strong>{{MonthlySalary}}</strong>, payable without\n        deductions for Provident Fund, Income Tax as per current company policy. A detailed offer letter with\n        salary breakup and terms will be shared closer to the joining date.\n      </p>\n\n      <p>The following conditions are applicable to this Pre-Placement Offer:</p>\n\n      <ul class=\"conditions-list\">\n        <li>Successful completion of your current academic program with no pending backlogs.</li>\n        <li>Submission of all required documents during onboarding.</li>\n        <li>Signing of the final employment agreement on or before the joining date.</li>\n      </ul>\n\n      <p>\n        Please confirm your acceptance of this PPO by replying to this email and sending a signed copy of this\n        letter within 7 days from the date of issue.\n      </p>\n\n      <p>\n        We are excited to welcome you to the Seecog team and look forward to a long and successful association\n        with you.\n      </p>\n\n      <!-- Closing & Signature with Stamp -->\n      <div class=\"closing\">\n        <p>Your’s sincerely,</p>\n        <p><strong>For Seecog Softwares Pvt. Ltd.</strong></p>\n\n        <div class=\"signature-block\">\n          <div class=\"signature-images\">\n            <img src=\"{{STAMP_SRC}}\" alt=\"Company Stamp\" />\n          </div>\n\n          <div class=\"signature-name\">Ms. Sonam Agarwal</div>\n          <div class=\"signature-title\">Email: sonam@seecogsoftwares.com</div>\n          <div class=\"signature-title\">Authorized Signatory</div>\n        </div>\n      </div>\n    </div>\n  </div>\n</body>\n</html>\n',0,'2025-11-29 08:02:28','2025-12-01 05:59:10',NULL),(8,'Employment Offer Letter','OFFER_LETTER','fas fa-file-signature','Formal employment offer letter detailing position, location, joining date, CTC structure, terms and conditions, required documents, leave policy, working hours, and candidate acknowledgement.','<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Offer Letter - Seecog Softwares Pvt. Ltd.</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f2f2f2;\n      font-family: var(--font-family);\n      color: var(--primary-text);\n      line-height: 1.5;\n      font-size: 13px;\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      position: relative;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    .letter-meta {\n      margin-top: 10px;\n      margin-bottom: 16px;\n      font-size: 12px;\n    }\n\n    .letter-meta .dated {\n      text-align: right;\n    }\n\n    .letter-title {\n      text-align: center;\n      margin: 8px 0 16px;\n      font-size: 18px;\n      font-weight: 700;\n      text-decoration: underline;\n      letter-spacing: 1px;\n    }\n\n    .letter-body {\n      font-size: 13px;\n      color: var(--primary-text);\n    }\n\n    .letter-body p {\n      margin: 4px 0 8px;\n      text-align: justify;\n    }\n\n    .letter-body p strong {\n      font-weight: 600;\n    }\n\n    .section-heading {\n      font-weight: 600;\n      margin-top: 10px;\n      margin-bottom: 4px;\n      font-size: 13px;\n      text-decoration: underline;\n    }\n\n    .section-subheading {\n      font-weight: 600;\n      margin-top: 6px;\n      margin-bottom: 4px;\n      font-size: 13px;\n    }\n\n    ul,\n    ol {\n      margin: 4px 0 8px 18px;\n      padding-left: 12px;\n    }\n\n    li {\n      margin: 2px 0;\n    }\n\n    .salary-table-wrapper {\n      margin: 8px 0 10px;\n      overflow-x: auto;\n    }\n\n    .salary-table {\n      width: 100%;\n      border-collapse: collapse;\n      font-size: 12px;\n    }\n\n    .salary-table th,\n    .salary-table td {\n      border: 1px solid var(--border);\n      padding: 6px 8px;\n      text-align: left;\n    }\n\n    .salary-table th {\n      background: #f5f5f5;\n      font-weight: 600;\n    }\n\n    .salary-table td:nth-child(2),\n    .salary-table td:nth-child(3),\n    .salary-table th:nth-child(2),\n    .salary-table th:nth-child(3) {\n      text-align: right;\n      white-space: nowrap;\n    }\n\n    .net-pay {\n      margin-top: 4px;\n      margin-bottom: 4px;\n      font-weight: 600;\n    }\n\n    .note-text {\n      font-size: 11px;\n      color: var(--muted-text);\n      margin-bottom: 10px;\n    }\n\n    .signature-section {\n      display: flex;\n      justify-content: space-between;\n      gap: 18px;\n      margin-top: 18px;\n      font-size: 12px;\n    }\n\n    .signature-block {\n      width: 50%;\n    }\n\n    .signature-block p {\n      margin: 2px 0;\n    }\n\n    .signature-label {\n      margin-top: 12px;\n    }\n\n    .signature-line {\n      margin-top: 24px;\n      border-top: 1px solid var(--border);\n      width: 150px;\n      display: inline-block;\n    }\n\n    .signer-name {\n      font-weight: 600;\n      margin-top: 6px;\n    }\n\n    .designation-text {\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .signature-images {\n      margin-top: 8px;\n      display: flex;\n      align-items: center;\n      gap: 12px;\n    }\n\n    .signature-images img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .candidate-ack-title {\n      font-weight: 600;\n      margin-bottom: 6px;\n      text-decoration: underline;\n    }\n\n    .candidate-ack-block {\n      margin-top: 8px;\n    }\n  </style>\n</head>\n\n<body>\n  <div class=\"a4-sheet\">\n    <!-- HEADER -->\n    <header class=\"letter-header\">\n      <div class=\"logo-wrapper\">\n        <img src=\"{{LOGO_SRC}}\" alt=\"Seecog Softwares Pvt. Ltd. Logo\" />\n      </div>\n\n      <div class=\"company-details\">\n        <h1>Seecog Softwares Pvt. Ltd.</h1>\n        <div class=\"cin\">\n          CIN: IN/U7220JH2021PTC017350\n        </div>\n        <div>\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi,<br />\n          Koramangala, Bengaluru, Karnataka 560030, India\n        </div>\n        <div>\n          Website:\n          <a href=\"https://seecogsoftwares.com\" target=\"_blank\">seecogsoftwares.com</a>\n        </div>\n        <div>Email: info@seecogsoftwares.com</div>\n        <div>Contact No: +91-7625067691</div>\n      </div>\n    </header>\n\n    <!-- META -->\n    <section class=\"letter-meta\">\n      <div class=\"dated\">\n        Dated:\n        <strong>{{offerDate}}</strong>\n      </div>\n    </section>\n\n    <!-- TITLE -->\n    <h2 class=\"letter-title\">OFFER LETTER</h2>\n\n    <!-- BODY -->\n    <main class=\"letter-body\">\n      <p>\n        Dear <strong>{{fullName}}</strong>,\n      </p>\n\n      <p>\n        We are delighted to offer you the position of\n        <strong>{{designation}}</strong> at\n        <strong>Seecog Softwares Pvt. Ltd.</strong>, following your successful\n        application and interview process. The details of your offer are as follows:\n      </p>\n\n      <!-- Position and Location -->\n      <p class=\"section-heading\">1. Position and Location</p>\n      <p>\n        You will be designated as <strong>{{designation}}</strong>. Your initial posting\n        location will be:\n      </p>\n      <p>\n        Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi,<br />\n        Koramangala, Bengaluru, Karnataka – 560030.\n      </p>\n      <p>\n        The company may transfer you to any other location in India based on operational\n        requirements.\n      </p>\n\n      <!-- Date of Joining -->\n      <p class=\"section-heading\">2. Date of Joining</p>\n      <p>\n        Your expected joining date is\n        <strong>{{joiningDate}}</strong>, subject to the satisfactory completion of\n        Background Verification (BGV).\n      </p>\n\n      <!-- Compensation Structure -->\n      <p class=\"section-heading\">3. Compensation Structure</p>\n      <p>\n        Your total Cost to Company (CTC) will be\n        <strong>₹{{ctc}}</strong> (<strong>{{ctcInWords}}</strong>) annually.\n        This CTC consists of a fixed gross salary component (paid monthly) and a\n        performance-linked variable pay component.\n        Statutory deductions such as Provident Fund (PF), Employee State Insurance (ESI)\n        (where applicable), Professional Tax and Income Tax (TDS) shall be made from your\n        salary as per prevailing laws.\n      </p>\n\n      <div class=\"salary-table-wrapper\">\n        <table class=\"salary-table\">\n          <thead>\n            <tr>\n              <th>Component</th>\n              <th>Monthly (₹)</th>\n              <th>Annually (₹)</th>\n            </tr>\n          </thead>\n          <tbody>\n            <tr>\n              <td>Basic Salary</td>\n              <td>₹{{basicMonth}}</td>\n              <td>₹{{basicAnnual}}</td>\n            </tr>\n            <tr>\n              <td>House Rent Allowance (HRA)</td>\n              <td>₹{{hraMonth}}</td>\n              <td>₹{{hraAnnual}}</td>\n            </tr>\n            <tr>\n              <td>Special Allowance</td>\n              <td>₹{{specialAllowanceMonth}}</td>\n              <td>₹{{specialAllowanceAnnual}}</td>\n            </tr>\n            <tr>\n              <td><strong>Fixed Gross Salary (before statutory deductions)</strong></td>\n              <td><strong>₹{{grossMonth}}</strong></td>\n              <td><strong>₹{{grossAnnual}}</strong></td>\n            </tr>\n            <tr>\n              <td>Variable Pay (CTC Component)</td>\n              <td>₹{{variablePayMonth}}</td>\n              <td>₹{{variablePayAnnual}}</td>\n            </tr>\n            <tr>\n              <td><strong>Total CTC</strong></td>\n              <td>-</td>\n              <td><strong>₹{{ctc}}</strong></td>\n            </tr>\n          </tbody>\n        </table>\n      </div>\n\n      <p class=\"net-pay\">\n        Net Take-Home (after PF, ESI, Professional Tax &amp; Income Tax (TDS),\n        excluding Variable Pay):\n        <strong>₹{{netPay}}</strong> per month\n      </p>\n\n      <p class=\"note-text\">\n        Approximate monthly deductions (based on current CTC):<br />\n        PF: <strong>₹{{pfEmployeeMonth}}</strong> &nbsp;|&nbsp;\n        ESI: <strong>₹{{esiEmployeeMonth}}</strong> &nbsp;|&nbsp;\n        Professional Tax: <strong>₹{{professionalTaxMonthly}}</strong> &nbsp;|&nbsp;\n        Income Tax (TDS): <strong>₹{{incomeTaxMonthly}}</strong>\n      </p>\n\n      <p class=\"note-text\">\n        Note: The above structure is an illustrative breakup. Statutory deductions\n        (PF, ESI where applicable, Professional Tax, and Income Tax/TDS) are calculated\n        as per prevailing laws and may vary slightly based on your chosen tax regime,\n        investment declarations and any changes in legislation. Variable pay, if\n        applicable, will be paid separately as per company policy and performance criteria.\n      </p>\n\n      <!-- Terms and Conditions -->\n      <p class=\"section-heading\">4. Terms and Conditions</p>\n\n      <p class=\"section-subheading\">a) Work Responsibilities</p>\n      <p>\n        You are expected to perform the duties of your role diligently. Additional\n        responsibilities or transfers (including deputation to clients or other\n        branches) may be assigned as required.\n      </p>\n\n      <p class=\"section-subheading\">b) Employment on Deputation</p>\n      <p>\n        You will remain a Seecog Softwares Pvt. Ltd. employee during any client\n        assignments and will not be eligible for client employment benefits.\n      </p>\n\n      <p class=\"section-subheading\">c) Conduct &amp; Ethics</p>\n      <p>\n        You are required to maintain strict confidentiality and ethical standards.\n        Outside employment is not allowed without prior written permission.\n      </p>\n\n      <p class=\"section-subheading\">d) Termination</p>\n      <p>\n        Either party may terminate this employment by providing\n        <strong>30 days\' written notice</strong> or salary in lieu of notice. If\n        you resign within one year, the company reserves the right to recover BGV or\n        other third-party expenses incurred on your behalf.\n      </p>\n\n      <p class=\"section-subheading\">e) Confidentiality</p>\n      <p>\n        Disclosure of proprietary, financial, or strategic information is strictly\n        prohibited during and after your employment with the company.\n      </p>\n\n      <!-- Documents Required -->\n      <p class=\"section-heading\">5. Documents Required on Joining</p>\n      <p>\n        Please carry the following documents (originals + 2 copies) on the date of joining:\n      </p>\n      <ul>\n        <li>Updated Resume</li>\n        <li>Educational Certificates and Mark Sheets (10th onwards)</li>\n        <li>Experience Letter and No Dues Certificate (if applicable)</li>\n        <li>Last Drawn Salary Slip (if applicable)</li>\n        <li>4 Recent Passport-size Color Photographs</li>\n        <li>Valid Photo ID (Aadhaar / PAN / Passport / Voter ID)</li>\n        <li>Permanent Address Proof</li>\n        <li>Cancelled Cheque</li>\n        <li>Two Professional References (if applicable)</li>\n      </ul>\n\n      <!-- Leave and Holidays -->\n      <p class=\"section-heading\">\n        6. Privilege and Casual Leave (Vacation) Entitlement and Paid Holidays\n      </p>\n      <ul>\n        <li>\n          You will be entitled to <strong>22 days of privilege leave</strong> per\n          completed calendar year of service. Privilege leave will be accrued on the\n          basis of five and a half days of leave for each three months of continuous\n          employment.\n        </li>\n        <li>\n          Privilege leave will be taken by you at times that will be determined\n          depending upon the requirements of the business activities of the Company\n          and the provisions of national law, unless the Company and you otherwise\n          specifically agree.\n        </li>\n        <li>\n          Privilege leave entitlement must be taken in the first calendar year in which\n          it may be discharged. However, you will be entitled to carry forward privilege\n          leave up to a maximum of twenty-two days.\n        </li>\n        <li>\n          You will be entitled to a maximum of <strong>8 sick leave days</strong> per\n          completed year of service.\n        </li>\n        <li>\n          You will enjoy <strong>10 paid holidays</strong> as established by the Company\n          in conformance with the laws of India.\n        </li>\n      </ul>\n\n      <!-- Hours and Business Travel -->\n      <p class=\"section-heading\">7. Hours and Business Travel</p>\n      <p>\n        The working hours under this Agreement will be forty (40) hours per week. The\n        Company business day is generally from <strong>10:00 AM to 6:00 PM</strong>.\n        Notwithstanding, your duties may require you to engage in travel on behalf of\n        the Company, and to work at hours required by the nature of the business of the\n        Company. You expressly agree to accept such reasonable travel and hours of work\n        without additional compensation.\n      </p>\n      <p>\n        Business travel and assignments outside of India will be notified to you in\n        advance, but you agree that you will not refuse such travel and assignments\n        without imperative reasons.\n      </p>\n\n      <!-- Benefits Plans -->\n      <p class=\"section-heading\">8. Benefits Plans</p>\n      <p>\n        You will be entitled to participate in the Company benefit plans, as may be\n        established from time to time, at such times as you qualify for them or, as the\n        case may be, as you are selected for participation in them. The Company reserves\n        the right to amend or discontinue such benefit programs in its sole discretion\n        and without compensation to you for such amendment or discontinuance.\n      </p>\n\n      <!-- Conditional Clause -->\n      <p class=\"section-heading\">9. Offer Validity and Acceptance</p>\n      <p>\n        This offer is subject to verification of your documents and background. Any\n        discrepancy may result in revocation of this offer or termination of employment.\n      </p>\n      <p>\n        To confirm acceptance, please reply by email and send back a signed copy of\n        this letter within <strong>2 working days</strong>.\n      </p>\n\n      <p>\n        We welcome you to the <strong>Seecog family</strong> and look forward to a\n        successful journey together.\n      </p>\n\n      <!-- SIGNATURES -->\n      <section class=\"signature-section\">\n        <!-- Company Signatory -->\n        <div class=\"signature-block\">\n          <p>Warm regards,</p>\n          <p><strong>For Seecog Softwares Pvt. Ltd.</strong></p>\n\n          <div class=\"signature-images\">\n            <img src=\"{{STAMP_SRC}}\" alt=\"Company Stamp\" />\n          </div>\n\n          <div class=\"signature-label signature-line\"></div>\n\n          <p class=\"signer-name\">Ms. Sonam Agarwal</p>\n          <p class=\"designation-text\">Manager – Talent Acquisition</p>\n          <p class=\"designation-text\">Email: sonam@seecogsoftwares.com</p>\n        </div>\n\n        <!-- Candidate Acknowledgement -->\n        <div class=\"signature-block\">\n          <p class=\"candidate-ack-title\">Candidate Acknowledgement</p>\n\n          <div class=\"candidate-ack-block\">\n            <p>\n              I, <strong>{{fullName}}</strong>, have read and understood the terms of\n              this Offer Letter and accept the offer.\n            </p>\n\n            <p class=\"signature-label\">\n              Signature:\n              <span class=\"signature-line\"></span>\n            </p>\n\n            <p class=\"signature-label\">\n              Date:\n              <span>______________________</span>\n            </p>\n          </div>\n        </div>\n      </section>\n    </main>\n  </div>\n</body>\n\n</html>\n',0,'2025-11-29 08:06:04','2025-12-06 05:24:17',NULL),(9,'Bonus Letter','BONUS_LETTER','fas fa-gift','Formal bonus announcement letter recognizing an employee’s performance and confirming the bonus amount, credit date, and discretionary nature of the payout.','<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Bonus Announcement Letter - Seecog Softwares Pvt. Ltd.</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f2f2f2;\n      font-family: var(--font-family);\n      color: var(--primary-text);\n      line-height: 1.5;\n      font-size: 13px;\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      position: relative;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    /* META / DATE */\n    .letter-meta {\n      margin-top: 10px;\n      margin-bottom: 16px;\n      font-size: 12px;\n    }\n\n    .letter-meta .dated {\n      text-align: right;\n    }\n\n    /* TITLE */\n    .letter-title {\n      text-align: center;\n      margin: 8px 0 16px;\n      font-size: 18px;\n      font-weight: 700;\n      text-decoration: underline;\n      letter-spacing: 1px;\n      text-transform: uppercase;\n    }\n\n    /* BODY */\n    .letter-body {\n      font-size: 13px;\n      color: var(--primary-text);\n    }\n\n    .letter-body p {\n      margin: 4px 0 8px;\n      text-align: justify;\n    }\n\n    .letter-body p strong {\n      font-weight: 600;\n    }\n\n    .meta-table-wrapper {\n      margin-bottom: 10px;\n    }\n\n    .meta-table {\n      width: 100%;\n      border-collapse: collapse;\n      font-size: 12px;\n    }\n\n    .meta-table td {\n      padding: 3px 0;\n      vertical-align: top;\n    }\n\n    .meta-label {\n      width: 130px;\n      font-weight: 600;\n      white-space: nowrap;\n    }\n\n    .meta-value {\n      padding-left: 4px;\n    }\n\n    /* SIGNATURES */\n    .signature-section {\n      display: flex;\n      justify-content: space-between;\n      gap: 18px;\n      margin-top: 24px;\n      font-size: 12px;\n    }\n\n    .signature-block {\n      width: 50%;\n    }\n\n    .signature-block p {\n      margin: 2px 0;\n    }\n\n    .signature-line {\n      margin-top: 24px;\n      border-top: 1px solid var(--border);\n      width: 150px;\n      display: inline-block;\n    }\n\n    .signer-name {\n      font-weight: 600;\n      margin-top: 6px;\n    }\n\n    .designation-text {\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .signature-images {\n      margin-top: 8px;\n      display: flex;\n      align-items: center;\n      gap: 12px;\n    }\n\n    .signature-images img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n  </style>\n</head>\n\n<body>\n  <div class=\"a4-sheet\">\n    <!-- HEADER -->\n    <header class=\"letter-header\">\n      <div class=\"logo-wrapper\">\n        <!-- Dynamic logo source -->\n        <img src=\"{{LOGO_SRC}}\" alt=\"Seecog Softwares Pvt. Ltd. Logo\" />\n      </div>\n\n      <div class=\"company-details\">\n        <h1>Seecog Softwares Pvt. Ltd.</h1>\n        <div class=\"cin\">\n          CIN: U7220JH2021PTC017350\n        </div>\n        <div>\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi,<br />\n          Koramangala, Bengaluru, Karnataka 560030, India\n        </div>\n        <div>\n          Website:\n          <a href=\"https://seecogsoftwares.com\" target=\"_blank\">seecogsoftwares.com</a>\n        </div>\n        <div>Email: info@seecogsoftwares.com</div>\n        <div>Contact No: +91-7625067691</div>\n      </div>\n    </header>\n\n    <!-- META -->\n    <section class=\"letter-meta\">\n      <div class=\"dated\">\n        Dated:\n        <strong>{{DATE}}</strong>\n      </div>\n    </section>\n\n    <!-- TITLE -->\n    <h2 class=\"letter-title\">BONUS ANNOUNCEMENT LETTER</h2>\n\n    <!-- BODY -->\n    <main class=\"letter-body\">\n      <!-- Employee Meta Details -->\n      <div class=\"meta-table-wrapper\">\n        <table class=\"meta-table\">\n          <tr>\n            <td class=\"meta-label\">Employee ID</td>\n            <td class=\"meta-value\">: {{EMP_ID}}</td>\n          </tr>\n          <tr>\n            <td class=\"meta-label\">Employee Name</td>\n            <td class=\"meta-value\">: {{EMP_NAME}}</td>\n          </tr>\n          <tr>\n            <td class=\"meta-label\">Designation</td>\n            <td class=\"meta-value\">: {{DESIGNATION}}</td>\n          </tr>\n        </table>\n      </div>\n\n      <!-- Letter Content -->\n      <p>\n        Dear <strong>{{EMP_NAME}}</strong>,\n      </p>\n\n      <p>\n        We are pleased to acknowledge and appreciate your valuable contributions to\n        <strong>Seecog Softwares Pvt. Ltd.</strong>\n      </p>\n\n      <p>\n        Your consistent performance, dedication, and teamwork have significantly\n        contributed to our company\'s growth and success.\n      </p>\n\n      <p>\n        As a token of appreciation, the management is happy to award you a bonus of\n        <strong>₹{{BONUS_AMOUNT}} (Rupees {{BONUS_IN_WORDS}} only)</strong>. This\n        bonus will be credited to your registered salary account on or before\n        <strong>{{CREDIT_DATE}}</strong>.\n      </p>\n\n      <p>\n        This reward reflects our recognition of your hard work and the value you\n        bring to the organization. We look forward to your continued excellence and\n        commitment in the coming months.\n      </p>\n\n      <p>\n        Please note that this bonus is discretionary and is based on performance and\n        business outcomes. It does not form part of your regular salary or\n        compensation structure.\n      </p>\n\n      <p>\n        Once again, thank you for your valuable contributions, and\n        <strong>congratulations</strong> on your well-deserved reward.\n      </p>\n\n      <!-- SIGNATURES -->\n      <section class=\"signature-section\">\n        <!-- Company Signatory -->\n        <div class=\"signature-block\">\n          <p>For Seecog Softwares Pvt. Ltd.</p>\n\n          <div class=\"signature-images\">\n            <!-- Dynamic stamp image (same pattern as offer letter) -->\n            <img src=\"{{STAMP_SRC}}\" alt=\"Company Stamp\" />\n          </div>\n\n          <div class=\"signature-line\"></div>\n\n          <p class=\"signer-name\">Ms. Sonam Agarwal</p>\n          <p class=\"designation-text\">Manager – Talent Acquisition</p>\n          <p class=\"designation-text\">Email: sonam@seecogsoftwares.com</p>\n          <p class=\"designation-text\">Authorized Signatory</p>\n        </div>\n\n        <!-- Empty block (optional for future use or employee ack) -->\n        <div class=\"signature-block\">\n          <!-- If you ever want an employee acknowledgement for bonus,\n               you can add fields here similar to offer letter. -->\n        </div>\n      </section>\n    </main>\n  </div>\n</body>\n\n</html>\n',0,'2025-11-29 08:08:48','2025-12-02 12:11:24',NULL),(10,'Salary Increment Letter','INCREMENT_LETTER','fas fa-arrow-trend-up','Formal salary increment letter notifying the employee of revised compensation, effective date, updated annual CTC, and appreciation for their performance and contributions.','<!DOCTYPE html>\n<html lang=\"en\">\n\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Salary Increment Notification - {{EMP_NAME}}</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f2f2f2;\n      font-family: var(--font-family);\n      color: var(--primary-text);\n      line-height: 1.5;\n      font-size: 13px;\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      position: relative;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primary-text);\n    }\n\n    .company-details .cin {\n      font-weight: 600;\n      font-size: 11px;\n    }\n\n    .company-details a {\n      color: var(--accent);\n      text-decoration: none;\n    }\n\n    .company-details a:hover {\n      text-decoration: underline;\n    }\n\n    /* META / DATE */\n    .letter-meta {\n      margin-top: 10px;\n      margin-bottom: 16px;\n      font-size: 12px;\n    }\n\n    .letter-meta .dated {\n      text-align: right;\n    }\n\n    /* TITLE */\n    .letter-title {\n      text-align: center;\n      margin: 8px 0 16px;\n      font-size: 18px;\n      font-weight: 700;\n      text-decoration: underline;\n      letter-spacing: 1px;\n    }\n\n    /* BODY */\n    .letter-body {\n      font-size: 13px;\n      color: var(--primary-text);\n    }\n\n    .letter-body p {\n      margin: 4px 0 8px;\n      text-align: justify;\n    }\n\n    .letter-body p strong {\n      font-weight: 600;\n    }\n\n    .meta-table-wrapper {\n      margin-bottom: 10px;\n    }\n\n    .meta-table {\n      width: 100%;\n      border-collapse: collapse;\n      font-size: 12px;\n    }\n\n    .meta-table td {\n      padding: 3px 0;\n      vertical-align: top;\n    }\n\n    .meta-label {\n      width: 130px;\n      font-weight: 600;\n      white-space: nowrap;\n    }\n\n    .meta-value {\n      padding-left: 4px;\n    }\n\n    /* SIGNATURES */\n    .signature-section {\n      margin-top: 24px;\n      font-size: 12px;\n    }\n\n    .signature-block p {\n      margin: 2px 0;\n    }\n\n    .signature-line {\n      margin-top: 24px;\n      border-top: 1px solid var(--border);\n      width: 150px;\n      display: inline-block;\n    }\n\n    .signer-name {\n      font-weight: 600;\n      margin-top: 6px;\n    }\n\n    .designation-text {\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .signature-images {\n      margin-top: 8px;\n      display: flex;\n      align-items: center;\n      gap: 12px;\n    }\n\n    .signature-images img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n  </style>\n</head>\n\n<body>\n  <div class=\"a4-sheet\">\n    <!-- HEADER -->\n    <header class=\"letter-header\">\n      <div class=\"logo-wrapper\">\n        <!-- Dynamic logo source -->\n        <img src=\"{{LOGO_SRC}}\" alt=\"Seecog Softwares Pvt. Ltd. Logo\" />\n      </div>\n\n      <div class=\"company-details\">\n        <h1>Seecog Softwares Pvt. Ltd.</h1>\n        <div class=\"cin\">\n          CIN: U7220JH2021PTC017350\n        </div>\n        <div>\n          Prestige Cube, Site No. 26, Laskar Hosur Road, Adugodi,<br />\n          Koramangala, Bengaluru, Karnataka 560030, India\n        </div>\n        <div>\n          Website:\n          <a href=\"https://seecogsoftwares.com\" target=\"_blank\">seecogsoftwares.com</a>\n        </div>\n        <div>Email: info@seecogsoftwares.com</div>\n        <div>Contact No: +91-7625067691</div>\n      </div>\n    </header>\n\n    <!-- META -->\n    <section class=\"letter-meta\">\n      <div class=\"dated\">\n        Dated:\n        <strong>{{OFFER_DATE}}</strong>\n      </div>\n    </section>\n\n    <!-- TITLE -->\n    <h2 class=\"letter-title\">SALARY INCREMENT NOTIFICATION</h2>\n\n    <!-- BODY -->\n    <main class=\"letter-body\">\n      <!-- Employee Meta -->\n      <div class=\"meta-table-wrapper\">\n        <table class=\"meta-table\">\n          <tr>\n            <td class=\"meta-label\">Employee Name:</td>\n            <td class=\"meta-value\">{{EMP_NAME}}</td>\n          </tr>\n          <tr>\n            <td class=\"meta-label\">Designation:</td>\n            <td class=\"meta-value\">{{DESIGNATION}}</td>\n          </tr>\n        </table>\n      </div>\n\n      <p>\n        Dear <strong>{{EMP_NAME}}</strong>,\n      </p>\n\n      <p>\n        We are pleased to acknowledge your dedication, performance, and valuable contribution to\n        <strong>Seecog Softwares Pvt. Ltd.</strong>\n      </p>\n\n      <p>\n        Based on your performance evaluation and the recommendation of your reporting manager,\n        the management has approved an increment in your compensation, effective\n        <strong>{{EFFECTIVE_DATE}}</strong>.\n      </p>\n\n      <p>\n        Your current compensation of <strong>₹{{AMOUNT}}</strong> will be revised to\n        <strong>₹{{REVISED_AMOUNT}}</strong>, amounting to an annual Cost to Company (CTC) of\n        <strong>₹{{REVISED_ANNUAL_CTC}}</strong>.\n      </p>\n\n      <p>\n        We trust that this recognition will motivate you to continue your excellent work and take on new\n        challenges with enthusiasm and responsibility. We sincerely appreciate your efforts and look forward\n        to your continued association and growth with the company.\n      </p>\n\n      <p>\n        Should you have any questions or require clarification regarding the revised salary structure,\n        please feel free to reach out to the HR department.\n      </p>\n\n      <p>\n        Once again, congratulations on your well-deserved increment.\n      </p>\n\n      <!-- CLOSING & SIGNATURE -->\n      <section class=\"signature-section\">\n        <p>Warm regards,</p>\n        <p><strong>For Seecog Softwares Pvt. Ltd.</strong></p>\n\n        <div class=\"signature-images\">\n          <!-- Dynamic stamp image -->\n          <img src=\"{{STAMP_SRC}}\" alt=\"Company Stamp\" />\n        </div>\n\n        <div class=\"signature-line\"></div>\n\n        <p class=\"signer-name\">Ms. Sonam Agarwal</p>\n        <p class=\"designation-text\">Manager – Talent Acquisition</p>\n        <p class=\"designation-text\">Email: sonam@seecogsoftwares.com</p>\n      </section>\n    </main>\n  </div>\n</body>\n\n</html>\n',0,'2025-11-29 08:11:28','2025-12-02 12:31:08',NULL),(11,'Relieving & Experience Letter','RELIEVING_LETTER','fas fa-door-open','Formal relieving and experience letter confirming an employee’s separation date, tenure, role, and appreciating their contribution while wishing them success in future endeavors.','<!DOCTYPE html>\n<html lang=\"en\">\n<head>\n  <meta charset=\"UTF-8\" />\n  <title>Relieving & Experience Letter - {{EMP_NAME}}</title>\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\" />\n\n  <style>\n    :root {\n      --primary-text: #222222;\n      --muted-text: #555555;\n      --accent: #0056b3;\n      --border: #dddddd;\n      --font-family: \"Segoe UI\", system-ui, -apple-system, BlinkMacSystemFont,\n        \"Helvetica Neue\", Arial, sans-serif;\n    }\n\n    * {\n      box-sizing: border-box;\n      font-family: var(--font-family);\n    }\n\n    body {\n      margin: 0;\n      padding: 0;\n      background: #f5f5f5;\n      font-size: 11px;\n      line-height: 1.6;\n      color: var(--primary-text);\n    }\n\n    .a4-sheet {\n      width: 210mm;\n      min-height: 297mm;\n      margin: 10mm auto;\n      background: #ffffff;\n      padding: 15mm 18mm;\n      box-shadow: 0 0 6mm rgba(0, 0, 0, 0.08);\n      position: relative;\n      border: 1px solid #d0d0d0;\n    }\n\n    /* PRINT STYLES */\n    @page {\n      size: A4;\n      margin: 15mm 15mm 15mm 15mm;\n    }\n\n    @media print {\n      body {\n        background: #ffffff;\n      }\n\n      .a4-sheet {\n        margin: 0;\n        width: auto;\n        min-height: auto;\n        padding: 0;\n        box-shadow: none;\n        border: none;\n        page-break-after: always;\n      }\n\n      .no-print {\n        display: none !important;\n      }\n    }\n\n    /* RESPONSIVE (screen) */\n    @media (max-width: 900px) {\n      .a4-sheet {\n        width: 100%;\n        min-height: auto;\n        margin: 0;\n        padding: 10px;\n        box-shadow: none;\n        border: none;\n      }\n\n      .letter-header {\n        flex-direction: row;\n        align-items: flex-start;\n        gap: 12px;\n      }\n\n      .company-details {\n        text-align: right;\n      }\n    }\n\n    /* HEADER (same as Offer Letter) */\n    .letter-header {\n      display: flex;\n      justify-content: flex-start;\n      align-items: flex-start;\n      gap: 16px;\n      border-bottom: 2px solid var(--border);\n      padding-bottom: 10px;\n      margin-bottom: 10px;\n    }\n\n    .logo-wrapper {\n      flex: 0 0 90px;\n    }\n\n    .logo-wrapper img {\n      max-height: 60px;\n      width: auto;\n      display: block;\n    }\n\n    .company-details {\n      flex: 1;\n      text-align: right;\n      font-size: 11px;\n      color: var(--muted-text);\n    }\n\n    .company-details h1 {\n      margin: 0 0 4px;\n      font-size: 16px;\n      font-weight: 700;\n      color: var(--primar